import React, { useState, useRef } from "react";
import "./App.css";
import Category from "./components/Category";
import SubCategory from "./components/SubCategory";
// ---------------- REACT ---------------------//

// VARIABLES 

// let url = "https://sheetsu.com/apis/v1.0bu/05c2c5a08ead";
let url = "./data.json";
const App = () => {


  // -----  GLOBAL STATES
  const catinput = useRef()
  const [data, setData] = useState([]);
  const [sub, setsub] = useState([]);


  // -----  FUNCTIONS


  // -----  fetching data
  if (data.length === 0) {
    fetch(url)
      .then((res) => res.json())
      .then((res) => setData(res))
      .then(() => console.log(data))
  }
  // -----  USER interface
  return data.length === 0 ?
    (<div> loading { data.length} </div>)
    : (<div className='container'>
      <div className="row">
        Logo here
        </div>

      <div className="row">
        <Category data={data} ref={catinput} />
      </div>
      <div className="row">
        <SubCategory data={sub} onClick={() => {
          // setsub(data.filter(cat => cat.cat === catinput.current.value))
          console.log(catinput.current);
        }} />
      </div>
    </div>)
};
export default App;
