import React, { useState } from "react";
import "./App.css";
import Category from "./components/Category";
import SubCategory from "./components/SubCategory";
// ---------------- REACT ---------------------//

// VARIABLES 

// let url = "https://sheetsu.com/apis/v1.0bu/05c2c5a08ead";
let url = "./data.json";
const App = () => {

  // -----  GLOBAL STATES
  const [data, setData] = useState([]);
  const [sub, setsub] = useState([]);
  const [subcatVal, setsubcatVal] = useState([]);


  // -----  fetching data
  if (data.length === 0) {
    fetch(url)
      .then((res) => res.json())
      .then((res) => setData(res))
  }

  function setcat(v) {
    setsub(data.filter(cat => cat.cat === v))
    console.log('value sub :', sub);
  }
  function subcatset(v) {
    setsubcatVal(sub.filter(cat => cat.title === v))
    console.log('value sub :', sub);
    console.log('value subcatVal :', subcatVal);
    console.log('value is :', v);
  }


  // -----  USER interface
  return data.length === 0 ?
    (<div> loading { data.length} </div>)
    : (<div className='container'>
      <div className="row">
        Logo here
        </div>

      <div className="row">
        <Category data={data} setcat={setcat} />
      </div>
      <div className="row" >
        <SubCategory data={sub} subcatset={subcatset} />
      </div>
      <div className="row" >
        <div className="form-group">
          <label htmlFor="exampleFormControlTextarea1">Example textarea</label>
          <textarea className="form-control" id="text" rows={3} defaultValue={subcatVal.map(cat => cat.msg)} />
        </div>

      </div>
    </div>)
};
export default App;
