import React, { useState } from "react";
import "./App.css";
import Category from "./components/Category";
import SubCategory from "./components/SubCategory";
// ---------------- REACT ---------------------//

// VARIABLES 

// let url = "https://sheetsu.com/apis/v1.0bu/05c2c5a08ead";
let url = "./data.json";
const App = () => {


  // -----  GLOBAL STATES
  const [data, setData] = useState([]);
  const [catval, setcatval] = useState([]);
  const [subcatval, setSubcatval] = useState([]);
  const [filtredsub, setfiltredsub] = useState([]);


  // -----  FUNCTIONS

  function handlecatval(e) {
    setcatval(e.target.value)
  }

  function handlesubcatval(e) {
    setSubcatval(e.target.value)
  }

  function filtersub() {
    let d = data.filter(cat => cat.cat === catval)
    setfiltredsub(d)

  }

  // -----  fetching data
  if (data.length === 0) {
    fetch(url)
      .then((res) => res.json())
      .then((res) => setData(res))
      .then(() => console.log(data))
  }
  // -----  USER interface
  return data.length === 0 ?
    (<div> loading { data.length} </div>)
    : (<div className='container'>
      <div className="row">
        Logo here
        </div>

      <div className="row">
        <Category data={data} value={catval} onChange={handlecatval} onClick={handlecatval} />
      </div>
      <div className="row">
        <SubCategory data={filtredsub} value={subcatval} onChange={han} onClick={filtersub} />
      </div>
    </div>)
};
export default App;
